const text = $json.stdout || $json.data || '';
let result;
try {
  result = JSON.parse(text);
} catch (e) {
  throw new Error('Failed to parse inspector_result.json: ' + e.message);
}
if (!result || (result.status !== 'approved' && result.status !== 'changes_requested')) {
  throw new Error('Inspector result has invalid status: ' + JSON.stringify(result));
}
// n8n expects an array of items
return [result];
