// Inspector callback body from agent_runner.py looks like:
// { success: boolean, output: string }
// We extract and validate a single JSON object from output.

const payload = $json; // data from Wait for Inspector webhook
const outputString = (payload.output ?? '').toString();

// Find the JSON object in the output (agents may add chatter/logs)
const firstBrace = outputString.indexOf('{');
const lastBrace = outputString.lastIndexOf('}');

if (firstBrace === -1 || lastBrace === -1) {
  throw new Error('Inspector output did not contain any JSON object');
}

const jsonStr = outputString.substring(firstBrace, lastBrace + 1);

let decision;
try {
  decision = JSON.parse(jsonStr);
} catch (e) {
  throw new Error('Failed to parse Inspector JSON: ' + (e && e.message ? e.message : e));
}

// Strict schema validation to enforce Inspector contract from prompts/inspector.prompt.md
if (!decision || (decision.status !== 'approved' && decision.status !== 'changes_requested')) {
  throw new Error('Inspector JSON has invalid or missing status: ' + JSON.stringify(decision));
}

if (!Array.isArray(decision.issues)) {
  throw new Error('Inspector JSON issues is not an array: ' + JSON.stringify(decision.issues));
}

for (const issue of decision.issues) {
  if (!issue) {
    throw new Error('Inspector JSON issue is null/undefined');
  }
  if (!['blocker', 'major', 'minor'].includes(issue.severity)) {
    throw new Error('Inspector JSON issue has invalid severity: ' + JSON.stringify(issue));
  }
  if (typeof issue.description !== 'string') {
    throw new Error('Inspector JSON issue.description is not a string: ' + JSON.stringify(issue));
  }
  if (!Array.isArray(issue.paths)) {
    throw new Error('Inspector JSON issue.paths is not an array: ' + JSON.stringify(issue));
  }
}

if (!Array.isArray(decision.next_tasks)) {
  throw new Error('Inspector JSON next_tasks is not an array: ' + JSON.stringify(decision.next_tasks));
}

for (const t of decision.next_tasks) {
  if (typeof t !== 'string') {
    throw new Error('Inspector JSON next_tasks contains non-string: ' + JSON.stringify(decision.next_tasks));
  }
}

// Success path
return { json: decision };
